import React, { useState, useEffect } from 'react';
import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [canceled, setCanceled] = useState(false); // New state for canceling fetch
  const retryInterval = 5000;
  let retryTimer = null;

  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('https://swapi.dev/api/films/');

      if (!response.ok) {
        throw new Error('Something went wrong......RETRYING');
      }
      const data = await response.json();

      const transformedMovies = data.results.map((movieData) => ({
        id: movieData.episode_id,
        title: movieData.title,
        openingText: movieData.opening_crawl,
        releaseDate: movieData.release_date,
      }));

      setMovies(transformedMovies);
    } catch (error) {
      if (!canceled) {
        setError(error.message);
        retryTimer = setTimeout(fetchMoviesHandler, retryInterval);
      }
    }

    setIsLoading(false);
  }

  const handleCancelClick = () => {
    clearTimeout(retryTimer);
    setCanceled(true); 
  };

  useEffect(() => {
    fetchMoviesHandler();
    return () => {
      clearTimeout(retryTimer);
    };
  }, [canceled]);

  let content = <p>Found no movies!</p>;
  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }
  if (error) {
    content = <p>{error}</p>;
  }
  if (isLoading) {
    content = <p>Loading....</p>;
  }

  return (
    <React.Fragment>
      <section>
        <button disabled={isLoading}>Fetch Movies</button>
        <button onClick={handleCancelClick}>Cancel Retry</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
